document.addEventListener("DOMContentLoaded",()=>{const v=document.querySelectorAll(".video-list-item"),y=document.querySelector(".video-player video"),h=document.querySelector(".video-placeholder"),a=document.querySelector(".video-player"),s=document.getElementById("sort-by"),i=document.getElementById("search-input");function f(){const e=i.value.toLowerCase(),t=Array.from(v).filter(t=>t.getAttribute("data-video-name").toLowerCase().includes(e));l(t)}function l(e){const t=s.value,l=e.sort((e,l)=>{if("asc"===t)return e.getAttribute("data-video-name").localeCompare(l.getAttribute("data-video-name"));if("desc"===t)return l.getAttribute("data-video-name").localeCompare(e.getAttribute("data-video-name"))}),n=document.querySelector(".video-list");n.innerHTML="",l.forEach(e=>{n.appendChild(e)})}v.forEach(e=>{e.addEventListener("click",()=>{y.src=e.getAttribute("data-video-path"),h.style.display="none",a.style.display="block",y.play()})}),s.addEventListener("change",f),i.addEventListener("input",f),l(Array.from(v))});